mapper.select:
SELECT * from ( SELECT
(select count(id) from t_fabulous where commentId = t1.id and userId = #{3}) fabulous,
t1.id, t1.userId, t1.articleId, t1.commentContent, date_format(t1.commentDate, '%m-%d %H:%i') commentDate, t1.fabulousCount, t2.userName, t2.portrait, (select count(id) from t_articleComment where commentId = t1.id and commentStage = 2) revertCount FROM t_articleComment t1 LEFT JOIN t_user t2 ON t1.userId = t2.id WHERE t1.articleId = #{2} AND t1.commentStage = 1 ) t ORDER BY t.fabulousCount DESC, t.revertCount DESC, t.commentDate DESC LIMIT #{0}, #{1}

SELECT
(select count(id) from t_fabulous where commentId = t1.id and userId = #{3}) fabulous,
t1.id, t1.userId, t1.articleId, t1.commentContent, date_format(t1.commentDate, '%m-%d %H:%i') commentDate, t1.fabulousCount, t2.userName, t2.portrait FROM t_articleComment t1 LEFT JOIN t_user t2 ON t1.userId = t2.id WHERE t1.commentId = #{2} AND t1.commentStage in (2, 3) ORDER BY t1.fabulousCount DESC, t1.commentDate DESC LIMIT #{0}, #{1}
